    if (items[i]["summary"].toString().toLowerCase().includes("cook")) {
        if (setcook === true) {
            setcook = false;
            msg = { payload: items[i] };
            m_out.push(msg);
        }
    } else {
        msg = { payload: items[i] };
        m_out.push(msg);
    }
}

return [m_out[0], m_out[1],m_out[2],m_out[3],m_out[4],m_out[5],m_out[6],m_out[7],m_out[8],m_out[9]];

